             //SEARCH BAR enrolled students
searchBar = document.getElementById('searchBar');
searchSubject = document.getElementById('searchSubject');
searchSubject.addEventListener('keyup',function (e) {
            let term = e.target.value.toLowerCase();
        const subjectContainer = document.getElementsByClassName('subject-container');
        Array.from(subjectContainer).forEach(function(container){
            let panel = container.lastElementChild;
            let li = panel.getElementsByTagName('li');
            
            Array.from(li).forEach(function (li) {
                let name = li.textContent.toLowerCase();
                if(name.indexOf(term) !=-1){
                    li.style.display = "block";
                    
            }else{
                li.style.display="none";
                }
                
            });
           
        });
} );
searchBar.addEventListener('keyup', function(e){
        let term = e.target.value.toLowerCase();
        const cardContainer = document.getElementsByClassName('card-container');
        Array.from(cardContainer).forEach(function(card){
            cardBody = card.firstElementChild.firstElementChild.nextElementSibling;
            let name = cardBody.firstElementChild.textContent.toLowerCase();
            let detail = cardBody.lastElementChild.textContent.toLowerCase();
            if (cardBody.childElementCount == 3){
             let degree= cardBody.firstElementChild.nextElementSibling.textContent.toLowerCase();
                if(name.indexOf(term) !=-1 || degree.indexOf(term) !=-1 || detail.indexOf(term) !=-1){
                    card.style.display="block";
                }else{
                    card.style.display="none";
                }
                
            }else {//students
                if(name.indexOf(term) !=-1 || detail.indexOf(term) !=-1 ){
                    card.style.display="block";
                }else{
                    card.style.display="none";
                }
        }
            
        });
});